      edit: 'ØªØ¹Ø¯ÙŠÙ„',
      delete: 'Ø­Ø°Ù',
      confirm: 'ØªØ£ÙƒÙŠØ¯',
      fruits: 'ÙÙˆØ§ÙƒÙ‡',
      vegetables: 'Ø®Ø¶Ø§Ø±',
      leafy: 'ÙˆØ±Ù‚ÙŠØ§Øª',
      baskets: 'Ø³Ù„Ø§Øª',
      deliverySettings: 'Ø¥Ø¹Ø¯Ø§Ø¯Ø§Øª Ø§Ù„ØªÙˆØµÙŠÙ„',
      deliveryPrice: 'Ø³Ø¹Ø± Ø§Ù„ØªÙˆØµÙŠÙ„ (Ø¯.Ùƒ)',
      kg: 'ÙƒÙŠÙ„Ùˆ',
      bunch: 'Ø³Ø­Ø§Ø±Ø©',
      piece: 'Ø­Ø¨Ø©',
      deleteConfirm: 'Ù‡Ù„ Ø£Ù†Øª Ù…ØªØ£ÙƒØ¯ Ù…Ù† Ø­Ø°Ù Ù‡Ø°Ø§ Ø§Ù„Ù…Ù†ØªØ¬ØŸ',
      unitsManagement: 'Ø¥Ø¯Ø§Ø±Ø© Ø§Ù„ÙˆØ­Ø¯Ø§Øª',
