import React, { useState } from 'react';
import './App.css';
import Header from './components/Header/Header.tsx';
import ProductGrid from './components/ProductGrid/ProductGrid.tsx';
import Cart from './components/Cart/Cart.tsx';
import { Language, Product, CartItem } from './types';

const App: React.FC = () => {
  const [language, setLanguage] = useState<Language>('ar');
  const [cartItems, setCartItems] = useState<CartItem[]>([]);
  const [isCartOpen, setIsCartOpen] = useState(false);

  // Sample products data
  const sampleProducts: Product[] = [
    {
      id: 1,
      name: { ar: 'تفاح أحمر', en: 'Red Apple' },
      category: 'fruits',
      price: 1.5,
      unit: { ar: 'كيلو', en: 'kg' },
      image: 'https://images.unsplash.com/photo-1560806887-1e4cd0b6cbd6?w=300',
      isPublished: true,
      stock: 50
    },
    {
      id: 2,
      name: { ar: 'موز', en: 'Banana' },
      category: 'fruits',
      price: 0.8,
      unit: { ar: 'سحارة', en: 'bunch' },
      image: 'https://images.unsplash.com/photo-1571771894821-ce9b6c11b08e?w=300',
      isPublished: true,
      stock: 30
    },
    {
      id: 3,
      name: { ar: 'طماطم', en: 'Tomato' },
      category: 'vegetables',
      price: 1.2,
      unit: { ar: 'كيلو', en: 'kg' },
      image: 'https://images.unsplash.com/photo-1546470427-e26264be0b5d?w=300',
      isPublished: true,
      stock: 25
    },
    {
      id: 4,
      name: { ar: 'خس', en: 'Lettuce' },
      category: 'leafy',
      price: 0.5,
      unit: { ar: 'حبة', en: 'piece' },
      image: 'https://images.unsplash.com/photo-1622206151226-18ca2c9ab4a1?w=300',
      isPublished: true,
      stock: 20
    }
  ];

  const addToCart = (product: Product, quantity: number) => {
    const existingItem = cartItems.find(item => item.product.id === product.id);

    if (existingItem) {
      setCartItems(cartItems.map(item =>
        item.product.id === product.id
          ? { ...item, quantity: item.quantity + quantity }
          : item
      ));
    } else {
      setCartItems([...cartItems, { product, quantity }]);
    }
  };

  const updateCartItem = (productId: number, quantity: number) => {
    if (quantity <= 0) {
      setCartItems(cartItems.filter(item => item.product.id !== productId));
    } else {
      setCartItems(cartItems.map(item =>
        item.product.id === productId
          ? { ...item, quantity }
          : item
      ));
    }
  };

  const clearCart = () => {
    setCartItems([]);
  };

  return (
    <div className={`App ${language === 'ar' ? 'rtl' : 'ltr'}`}>
      <Header 
        language={language}
        onLanguageChange={setLanguage}
        cartItemsCount={cartItems.reduce((total, item) => total + item.quantity, 0)}
        onCartClick={() => setIsCartOpen(!isCartOpen)}
      />

      <main className="main-content">
        <ProductGrid 
          products={sampleProducts}
          language={language}
          onAddToCart={addToCart}
        />
      </main>

      {isCartOpen && (
        <Cart
          items={cartItems}
          language={language}
          onUpdateItem={updateCartItem}
          onClearCart={clearCart}
          onClose={() => setIsCartOpen(false)}
        />
      )}
    </div>
  );
};

export default App;
