import { Language } from '../../types';
import './UserProfile.css';

interface UserProfileProps {
  language: Language;
  userType: 'admin' | 'user';
  userName: string;
  userEmail: string;
  onLogout: () => void;
  onAdminPanel?: () => void;
}

const UserProfile: React.FC<UserProfileProps> = ({
  language,
  userType,
  userName,
  userEmail,
  onLogout,
  onAdminPanel
}) => {
  const texts = {
    ar: {
      welcome: 'ŸÖÿ±ÿ≠ÿ®ÿßŸã',
      admin: 'ŸÖÿØŸäÿ± ÿßŸÑŸÜÿ∏ÿßŸÖ',
      user: 'ÿπÿ∂Ÿà',
      adminPanel: 'ŸÑŸàÿ≠ÿ© ÿßŸÑÿ•ÿØÿßÿ±ÿ©',
      logout: 'ÿ™ÿ≥ÿ¨ŸäŸÑ ÿÆÿ±Ÿàÿ¨',
      profile: 'ÿßŸÑŸÖŸÑŸÅ ÿßŸÑÿ¥ÿÆÿµŸä',
      orders: 'ÿ∑ŸÑÿ®ÿßÿ™Ÿä',
      settings: 'ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™'
    },
    en: {
      welcome: 'Welcome',
      admin: 'Administrator',
      user: 'Member',
      adminPanel: 'Admin Panel',
      logout: 'Logout',
      profile: 'Profile',
      orders: 'My Orders',
      settings: 'Settings'
    }
  };

  const currentTexts = texts[language];

  return (
    <div className="user-profile-dropdown">
      <div className="profile-header">
        <div className="user-avatar">
          {userType === 'admin' ? 'üëë' : 'üë§'}
        </div>
        <div className="user-info">
          <h4>{currentTexts.welcome}, {userName}</h4>
          <p className={`user-type ${userType}`}>
            {userType === 'admin' ? currentTexts.admin : currentTexts.user}
          </p>
          <span className="user-email">{userEmail}</span>
        </div>
      </div>

      <div className="profile-actions">
        {userType === 'admin' && onAdminPanel && (
          <button className="profile-btn admin-btn" onClick={onAdminPanel}>
            ‚öôÔ∏è {currentTexts.adminPanel}
          </button>
        )}

        <button className="profile-btn">
          üë§ {currentTexts.profile}
        </button>

        <button className="profile-btn">
          üì¶ {currentTexts.orders}
        </button>

        <button className="profile-btn">
          ‚öôÔ∏è {currentTexts.settings}
        </button>

        <button className="profile-btn logout-btn" onClick={onLogout}>
          üö™ {currentTexts.logout}
        </button>
      </div>
    </div>
  );
};

export default UserProfile;
