import React, { useState } from 'react';
import { Language } from '../../types';
import './AdminLogin.css';

interface AdminLoginProps {
  language: Language;
  onLogin: () => void;
}

const AdminLogin: React.FC<AdminLoginProps> = ({ language, onLogin }) => {
  const [credentials, setCredentials] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Admin credentials
  const ADMIN_EMAIL = 'summit_kw@hotmail.com';
  const ADMIN_PASSWORD = 'Ff91998910';

  const texts = {
    ar: {
      title: 'ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ ÿßŸÑŸÖÿ¥ÿ±ŸÅ',
      email: 'ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä',
      password: 'ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±',
      login: 'ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ',
      emailPlaceholder: 'ÿ£ÿØÿÆŸÑ ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä',
      passwordPlaceholder: 'ÿ£ÿØÿÆŸÑ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±',
      invalidCredentials: 'ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿØÿÆŸàŸÑ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©',
      welcome: 'ŸÖÿ±ÿ≠ÿ®ÿßŸã ÿ®ŸÉ ŸÅŸä ŸÑŸàÿ≠ÿ© ÿßŸÑÿ•ÿØÿßÿ±ÿ©',
      enterCredentials: 'Ÿäÿ±ÿ¨Ÿâ ÿ•ÿØÿÆÿßŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ¥ÿ±ŸÅ ŸÑŸÑÿØÿÆŸàŸÑ',
      backToSite: 'ÿßŸÑÿπŸàÿØÿ© ŸÑŸÑŸÖŸàŸÇÿπ',
      adminPanel: 'ŸÑŸàÿ≠ÿ© ÿßŸÑÿ•ÿØÿßÿ±ÿ©',
      secureLogin: 'ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ ÿ¢ŸÖŸÜ'
    },
    en: {
      title: 'Admin Login',
      email: 'Email',
      password: 'Password',
      login: 'Login',
      emailPlaceholder: 'Enter email address',
      passwordPlaceholder: 'Enter password',
      invalidCredentials: 'Invalid credentials',
      welcome: 'Welcome to Admin Panel',
      enterCredentials: 'Please enter admin credentials to continue',
      backToSite: 'Back to Site',
      adminPanel: 'Admin Panel',
      secureLogin: 'Secure Login'
    }
  };

  const currentTexts = texts[language];

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    // Simulate loading time
    await new Promise(resolve => setTimeout(resolve, 1000));

    if (credentials.email === ADMIN_EMAIL && credentials.password === ADMIN_PASSWORD) {
      // Store login state
      localStorage.setItem('adminLoggedIn', 'true');
      localStorage.setItem('adminEmail', ADMIN_EMAIL);
      onLogin();
    } else {
      setError(currentTexts.invalidCredentials);
    }

    setIsLoading(false);
  };

  const handleBackToSite = () => {
    window.location.href = '/';
  };

  return (
    <div className="admin-login-overlay">
      <div className="admin-login-container">
        <div className="login-header">
          <div className="logo-section">
            <h1 className="site-name">ü•≠ ŸÅŸÉŸáÿßŸÜŸä</h1>
            <p className="admin-subtitle">{currentTexts.adminPanel}</p>
          </div>
          <div className="secure-badge">
            <span>üîí {currentTexts.secureLogin}</span>
          </div>
        </div>

        <div className="login-content">
          <h2>{currentTexts.title}</h2>
          <p className="login-description">{currentTexts.enterCredentials}</p>

          {error && (
            <div className="error-message">
              <span>‚ö†Ô∏è {error}</span>
            </div>
          )}

          <form onSubmit={handleSubmit} className="login-form">
            <div className="form-group">
              <label htmlFor="email">{currentTexts.email}</label>
              <input
                type="email"
                id="email"
                value={credentials.email}
                onChange={(e) => setCredentials({...credentials, email: e.target.value})}
                placeholder={currentTexts.emailPlaceholder}
                required
                className="form-input"
              />
            </div>

            <div className="form-group">
              <label htmlFor="password">{currentTexts.password}</label>
              <input
                type="password"
                id="password"
                value={credentials.password}
                onChange={(e) => setCredentials({...credentials, password: e.target.value})}
                placeholder={currentTexts.passwordPlaceholder}
                required
                className="form-input"
              />
            </div>

            <button 
              type="submit" 
              className={`login-btn ${isLoading ? 'loading' : ''}`}
              disabled={isLoading}
            >
              {isLoading ? (
                <span className="loading-spinner">‚è≥</span>
              ) : (
                <span>üîë {currentTexts.login}</span>
              )}
            </button>
          </form>

          <button 
            onClick={handleBackToSite}
            className="back-to-site-btn"
          >
            ‚Üê {currentTexts.backToSite}
          </button>
        </div>

        <div className="login-footer">
          <p>¬© 2025 ŸÅŸÉŸáÿßŸÜŸä - {currentTexts.welcome}</p>
        </div>
      </div>
    </div>
  );
};

export default AdminLogin;
