import React, { useState } from 'react';
import { Language } from '../../types';
import './Login.css';

interface LoginProps {
  language: Language;
  onLogin: (userType: 'admin' | 'user', userEmail: string) => void;
  onClose: () => void;
}

const Login: React.FC<LoginProps> = ({ language, onLogin, onClose }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [credentials, setCredentials] = useState({
    email: '',
    password: '',
    name: '',
    phone: '',
    confirmPassword: ''
  });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Admin credentials
  const ADMIN_EMAIL = 'summit_kw@hotmail.com';
  const ADMIN_PASSWORD = 'Ff91998910';

  const texts = {
    ar: {
      loginTitle: 'ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ',
      registerTitle: 'ÿ•ŸÜÿ¥ÿßÿ° ÿ≠ÿ≥ÿßÿ® ÿ¨ÿØŸäÿØ',
      email: 'ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä',
      password: 'ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±',
      confirmPassword: 'ÿ™ÿ£ŸÉŸäÿØ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±',
      name: 'ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑŸÉÿßŸÖŸÑ',
      phone: 'ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ',
      login: 'ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ',
      register: 'ÿ•ŸÜÿ¥ÿßÿ° ÿ≠ÿ≥ÿßÿ®',
      emailPlaceholder: 'ÿ£ÿØÿÆŸÑ ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä',
      passwordPlaceholder: 'ÿ£ÿØÿÆŸÑ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±',
      confirmPasswordPlaceholder: 'ÿ£ÿπÿØ ÿ•ÿØÿÆÿßŸÑ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±',
      namePlaceholder: 'ÿ£ÿØÿÆŸÑ ÿßÿ≥ŸÖŸÉ ÿßŸÑŸÉÿßŸÖŸÑ',
      phonePlaceholder: 'ÿ£ÿØÿÆŸÑ ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ',
      switchToRegister: 'ŸÑŸäÿ≥ ŸÑÿØŸäŸÉ ÿ≠ÿ≥ÿßÿ®ÿü ÿ•ŸÜÿ¥ÿßÿ° ÿ≠ÿ≥ÿßÿ® ÿ¨ÿØŸäÿØ',
      switchToLogin: 'ŸÑÿØŸäŸÉ ÿ≠ÿ≥ÿßÿ®ÿü ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ',
      invalidCredentials: 'ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿØÿÆŸàŸÑ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©',
      emailExists: 'ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖÿ≥ÿ®ŸÇÿßŸã',
      passwordMismatch: 'ŸÉŸÑŸÖÿ™ÿß ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∫Ÿäÿ± ŸÖÿ™ÿ∑ÿßÿ®ŸÇÿ™ÿßŸÜ',
      registrationSuccess: 'ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ≠ÿ≥ÿßÿ® ÿ®ŸÜÿ¨ÿßÿ≠',
      welcome: 'ŸÖÿ±ÿ≠ÿ®ÿßŸã ÿ®ŸÉ',
      adminWelcome: 'ŸÖÿ±ÿ≠ÿ®ÿßŸã ÿ®ŸÉ ŸÅŸä ŸÑŸàÿ≠ÿ© ÿßŸÑÿ•ÿØÿßÿ±ÿ©',
      userWelcome: 'ŸÖÿ±ÿ≠ÿ®ÿßŸã ÿ®ŸÉ ŸÅŸä ŸÅŸÉŸáÿßŸÜŸä',
      close: 'ÿ•ÿ∫ŸÑÿßŸÇ',
      fillAllFields: 'Ÿäÿ±ÿ¨Ÿâ ŸÖŸÑÿ° ÿ¨ŸÖŸäÿπ ÿßŸÑÿ≠ŸÇŸàŸÑ',
      secureLogin: 'ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ ÿ¢ŸÖŸÜ'
    },
    en: {
      loginTitle: 'Login',
      registerTitle: 'Create New Account',
      email: 'Email',
      password: 'Password',
      confirmPassword: 'Confirm Password',
      name: 'Full Name',
      phone: 'Phone Number',
      login: 'Login',
      register: 'Sign Up',
      emailPlaceholder: 'Enter email address',
      passwordPlaceholder: 'Enter password',
      confirmPasswordPlaceholder: 'Re-enter password',
      namePlaceholder: 'Enter your full name',
      phonePlaceholder: 'Enter phone number',
      switchToRegister: "Don't have an account? Sign up",
      switchToLogin: 'Have an account? Login',
      invalidCredentials: 'Invalid credentials',
      emailExists: 'Email already exists',
      passwordMismatch: 'Passwords do not match',
      registrationSuccess: 'Account created successfully',
      welcome: 'Welcome',
      adminWelcome: 'Welcome to Admin Panel',
      userWelcome: 'Welcome to Fakahani',
      close: 'Close',
      fillAllFields: 'Please fill all fields',
      secureLogin: 'Secure Login'
    }
  };

  const currentTexts = texts[language];

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    if (isLogin) {
      // Login logic
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Check if admin
      if (credentials.email === ADMIN_EMAIL && credentials.password === ADMIN_PASSWORD) {
        localStorage.setItem('userLoggedIn', 'true');
        localStorage.setItem('userEmail', ADMIN_EMAIL);
        localStorage.setItem('userType', 'admin');
        localStorage.setItem('userName', 'ŸÖÿØŸäÿ± ÿßŸÑŸÜÿ∏ÿßŸÖ');
        onLogin('admin', ADMIN_EMAIL);
        return;
      }

      // Check regular users from localStorage
      const users = JSON.parse(localStorage.getItem('registeredUsers') || '[]');
      const user = users.find((u: any) => u.email === credentials.email && u.password === credentials.password);

      if (user) {
        localStorage.setItem('userLoggedIn', 'true');
        localStorage.setItem('userEmail', user.email);
        localStorage.setItem('userType', 'user');
        localStorage.setItem('userName', user.name);
        onLogin('user', user.email);
      } else {
        setError(currentTexts.invalidCredentials);
      }
    } else {
      // Registration logic
      if (!credentials.name || !credentials.email || !credentials.password || !credentials.phone) {
        setError(currentTexts.fillAllFields);
        setIsLoading(false);
        return;
      }

      if (credentials.password !== credentials.confirmPassword) {
        setError(currentTexts.passwordMismatch);
        setIsLoading(false);
        return;
      }

      await new Promise(resolve => setTimeout(resolve, 1000));

      // Check if email already exists
      const users = JSON.parse(localStorage.getItem('registeredUsers') || '[]');
      if (users.find((u: any) => u.email === credentials.email) || credentials.email === ADMIN_EMAIL) {
        setError(currentTexts.emailExists);
        setIsLoading(false);
        return;
      }

      // Register new user
      const newUser = {
        id: Date.now(),
        name: credentials.name,
        email: credentials.email,
        password: credentials.password,
        phone: credentials.phone,
        registeredAt: new Date().toISOString()
      };

      users.push(newUser);
      localStorage.setItem('registeredUsers', JSON.stringify(users));

      // Auto login after registration
      localStorage.setItem('userLoggedIn', 'true');
      localStorage.setItem('userEmail', newUser.email);
      localStorage.setItem('userType', 'user');
      localStorage.setItem('userName', newUser.name);
      onLogin('user', newUser.email);
    }

    setIsLoading(false);
  };

  return (
    <div className="login-overlay">
      <div className="login-container">
        <div className="login-header">
          <div className="logo-section">
            <h1 className="site-name">ü•≠ ŸÅŸÉŸáÿßŸÜŸä</h1>
            <p className="login-subtitle">{currentTexts.secureLogin}</p>
          </div>
          <button className="close-btn" onClick={onClose}>‚úï</button>
        </div>

        <div className="login-content">
          <h2>{isLogin ? currentTexts.loginTitle : currentTexts.registerTitle}</h2>

          {error && (
            <div className="error-message">
              <span>‚ö†Ô∏è {error}</span>
            </div>
          )}

          <form onSubmit={handleSubmit} className="login-form">
            {!isLogin && (
              <div className="form-group">
                <label htmlFor="name">{currentTexts.name}</label>
                <input
                  type="text"
                  id="name"
                  value={credentials.name}
                  onChange={(e) => setCredentials({...credentials, name: e.target.value})}
                  placeholder={currentTexts.namePlaceholder}
                  required={!isLogin}
                  className="form-input"
                />
              </div>
            )}

            <div className="form-group">
              <label htmlFor="email">{currentTexts.email}</label>
              <input
                type="email"
                id="email"
                value={credentials.email}
                onChange={(e) => setCredentials({...credentials, email: e.target.value})}
                placeholder={currentTexts.emailPlaceholder}
                required
                className="form-input"
              />
            </div>

            {!isLogin && (
              <div className="form-group">
                <label htmlFor="phone">{currentTexts.phone}</label>
                <input
                  type="tel"
                  id="phone"
                  value={credentials.phone}
                  onChange={(e) => setCredentials({...credentials, phone: e.target.value})}
                  placeholder={currentTexts.phonePlaceholder}
                  required={!isLogin}
                  className="form-input"
                />
              </div>
            )}

            <div className="form-group">
              <label htmlFor="password">{currentTexts.password}</label>
              <input
                type="password"
                id="password"
                value={credentials.password}
                onChange={(e) => setCredentials({...credentials, password: e.target.value})}
                placeholder={currentTexts.passwordPlaceholder}
                required
                className="form-input"
              />
            </div>

            {!isLogin && (
              <div className="form-group">
                <label htmlFor="confirmPassword">{currentTexts.confirmPassword}</label>
                <input
                  type="password"
                  id="confirmPassword"
                  value={credentials.confirmPassword}
                  onChange={(e) => setCredentials({...credentials, confirmPassword: e.target.value})}
                  placeholder={currentTexts.confirmPasswordPlaceholder}
                  required={!isLogin}
                  className="form-input"
                />
              </div>
            )}

            <button 
              type="submit" 
              className={`login-btn ${isLoading ? 'loading' : ''}`}
              disabled={isLoading}
            >
              {isLoading ? (
                <span className="loading-spinner">‚è≥</span>
              ) : (
                <span>
                  {isLogin ? 'üîë' : 'üë§'} {isLogin ? currentTexts.login : currentTexts.register}
                </span>
              )}
            </button>
          </form>

          <button 
            onClick={() => {
              setIsLogin(!isLogin);
              setError('');
              setCredentials({
                email: '',
                password: '',
                name: '',
                phone: '',
                confirmPassword: ''
              });
            }}
            className="switch-mode-btn"
          >
            {isLogin ? currentTexts.switchToRegister : currentTexts.switchToLogin}
          </button>
        </div>

        <div className="login-footer">
          <p>¬© 2025 ŸÅŸÉŸáÿßŸÜŸä - {currentTexts.welcome}</p>
        </div>
      </div>
    </div>
  );
};

export default Login;
