    { key: 'leafy', label: currentTexts.leafy },
    { key: 'baskets', label: currentTexts.baskets },
  ];

  // Use the managed units instead of hardcoded options
  const unitOptions = units;

  const [formData, setFormData] = useState({
    nameAr: '',
    nameEn: '',
    category: 'fruits' as ProductCategory,
    units: [{ id: 1, unit: { ar: 'ÙƒÙŠÙ„Ùˆ', en: 'kg' }, price: 0, isDefault: true }],
    stock: 0,
    images: [] as string[],
    imageFiles: [] as File[],
    tags: [] as string[],
    descriptionAr: '',
    descriptionEn: '',
    isPublished: true,
  });

  const resetForm = () => {
    setFormData({
      nameAr: '',
      nameEn: '',
      category: 'fruits',
      units: [{ id: 1, unit: { ar: 'ÙƒÙŠÙ„Ùˆ', en: 'kg' }, price: 0, isDefault: true }],
      stock: 0,
      images: [],
      imageFiles: [],
      tags: [],
      descriptionAr: '',
      descriptionEn: '',
      isPublished: true,
    });
    setEditingProduct(null);
  };

