import React, { useState } from 'react';
import { CartItem, Language } from '../../types';
import './Cart.css';

interface CartProps {
  items: CartItem[];
  language: Language;
  onUpdateItem: (productId: number, quantity: number) => void;
  onClearCart: () => void;
  onClose: () => void;
}

const Cart: React.FC<CartProps> = ({
  items,
  language,
  onUpdateItem,
  onClearCart,
  onClose,
}) => {
  const [deliveryPrice] = useState(2.000); // ÿ≥ÿπÿ± ÿßŸÑÿ™ŸàÿµŸäŸÑ ÿßŸÑÿ´ÿßÿ®ÿ™
  const [paymentMethod, setPaymentMethod] = useState<'link' | 'cash'>('cash');
  const [showCheckout, setShowCheckout] = useState(false);

  const texts = {
    ar: {
      cart: 'ÿßŸÑÿ≥ŸÑÿ©',
      empty: 'ÿßŸÑÿ≥ŸÑÿ© ŸÅÿßÿ±ÿ∫ÿ©',
      subtotal: 'ÿßŸÑŸÖÿ¨ŸÖŸàÿπ ÿßŸÑŸÅÿ±ÿπŸä',
      delivery: 'ÿßŸÑÿ™ŸàÿµŸäŸÑ',
      total: 'ÿßŸÑŸÖÿ¨ŸÖŸàÿπ ÿßŸÑŸÉŸÑŸä',
      clear: 'ÿ•ŸÅÿ±ÿßÿ∫ ÿßŸÑÿ≥ŸÑÿ©',
      checkout: 'ÿ•ÿ™ŸÖÿßŸÖ ÿßŸÑÿ∑ŸÑÿ®',
      paymentMethod: 'ÿ∑ÿ±ŸäŸÇÿ© ÿßŸÑÿØŸÅÿπ',
      cash: 'ÿØŸÅÿπ ŸÜŸÇÿØŸä',
      link: 'ÿØŸÅÿπ ŸÑŸäŸÜŸÉ',
      close: 'ÿ•ÿ∫ŸÑÿßŸÇ',
      currency: 'ÿØ.ŸÉ',
      quantity: 'ÿßŸÑŸÉŸÖŸäÿ©',
      price: 'ÿßŸÑÿ≥ÿπÿ±',
      continueShopping: 'ŸÖÿ™ÿßÿ®ÿπÿ© ÿßŸÑÿ™ÿ≥ŸàŸÇ'
    },
    en: {
      cart: 'Cart',
      empty: 'Cart is empty',
      subtotal: 'Subtotal',
      delivery: 'Delivery',
      total: 'Total',
      clear: 'Clear Cart',
      checkout: 'Checkout',
      paymentMethod: 'Payment Method',
      cash: 'Cash Payment',
      link: 'Link Payment',
      close: 'Close',
      currency: 'KWD',
      quantity: 'Quantity',
      price: 'Price',
      continueShopping: 'Continue Shopping'
    }
  };

  const currentTexts = texts[language];

  const subtotal = items.reduce((sum, item) => sum + (item.product.price * item.quantity), 0);
  const total = subtotal + deliveryPrice;

  const handleQuantityChange = (productId: number, newQuantity: number) => {
    onUpdateItem(productId, newQuantity);
  };

  const handleRemoveItem = (productId: number) => {
    onUpdateItem(productId, 0);
  };

  if (items.length === 0) {
    return (
      <div className="cart-overlay" onClick={onClose}>
        <div className="cart-container empty" onClick={(e) => e.stopPropagation()}>
          <div className="cart-header">
            <h2>{currentTexts.cart}</h2>
            <button className="close-btn" onClick={onClose}>‚úï</button>
          </div>
          <div className="empty-cart">
            <span className="empty-icon">üõí</span>
            <p>{currentTexts.empty}</p>
            <button className="continue-btn" onClick={onClose}>
              {currentTexts.continueShopping}
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="cart-overlay" onClick={onClose}>
      <div className="cart-container" onClick={(e) => e.stopPropagation()}>
        <div className="cart-header">
          <h2>{currentTexts.cart}</h2>
          <button className="close-btn" onClick={onClose}>‚úï</button>
        </div>

        <div className="cart-content">
          {/* Cart Items */}
          <div className="cart-items">
            {items.map((item) => (
              <div key={item.product.id} className="cart-item">
                <div className="item-image">
                  <img src={item.product.image} alt={item.product.name[language]} />
                </div>

                <div className="item-details">
                  <h4 className="item-name">{item.product.name[language]}</h4>
                  <p className="item-unit">{item.product.unit[language]}</p>
                  <p className="item-price">
                    {item.product.price.toFixed(3)} {currentTexts.currency}
                  </p>
                </div>

                <div className="item-controls">
                  <div className="quantity-controls">
                    <button
                      className="qty-btn"
                      onClick={() => handleQuantityChange(item.product.id, item.quantity - 1)}
                      disabled={item.quantity <= 1}
                    >
                      -
                    </button>
                    <span className="quantity">{item.quantity}</span>
                    <button
                      className="qty-btn"
                      onClick={() => handleQuantityChange(item.product.id, item.quantity + 1)}
                    >
                      +
                    </button>
                  </div>

                  <div className="item-total">
                    {(item.product.price * item.quantity).toFixed(3)} {currentTexts.currency}
                  </div>

                  <button
                    className="remove-btn"
                    onClick={() => handleRemoveItem(item.product.id)}
                  >
                    üóëÔ∏è
                  </button>
                </div>
              </div>
            ))}
          </div>

          {/* Cart Summary */}
          <div className="cart-summary">
            <div className="summary-line">
              <span>{currentTexts.subtotal}:</span>
              <span>{subtotal.toFixed(3)} {currentTexts.currency}</span>
            </div>
            <div className="summary-line">
              <span>{currentTexts.delivery}:</span>
              <span>{deliveryPrice.toFixed(3)} {currentTexts.currency}</span>
            </div>
            <div className="summary-line total-line">
              <span>{currentTexts.total}:</span>
              <span>{total.toFixed(3)} {currentTexts.currency}</span>
            </div>
          </div>

          {/* Payment Method */}
          <div className="payment-section">
            <h3>{currentTexts.paymentMethod}</h3>
            <div className="payment-options">
              <label className="payment-option">
                <input
                  type="radio"
                  name="payment"
                  value="cash"
                  checked={paymentMethod === 'cash'}
                  onChange={(e) => setPaymentMethod(e.target.value as 'cash' | 'link')}
                />
                <span>üíµ {currentTexts.cash}</span>
              </label>
              <label className="payment-option">
                <input
                  type="radio"
                  name="payment"
                  value="link"
                  checked={paymentMethod === 'link'}
                  onChange={(e) => setPaymentMethod(e.target.value as 'cash' | 'link')}
                />
                <span>üí≥ {currentTexts.link}</span>
              </label>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="cart-actions">
            <button className="clear-btn" onClick={onClearCart}>
              {currentTexts.clear}
            </button>
            <button className="checkout-btn" onClick={() => setShowCheckout(true)}>
              {currentTexts.checkout}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Cart;
