import React from 'react';
import { Language } from '../../types';
import './Header.css';

interface HeaderProps {
  language: Language;
  onLanguageChange: (lang: Language) => void;
  cartItemsCount: number;
  onCartClick: () => void;
  onAdminClick: () => void;
}

const Header: React.FC<HeaderProps> = ({
  language,
  onLanguageChange,
  cartItemsCount,
  onCartClick,
  onAdminClick,
}) => {
  const texts = {
    ar: {
      title: 'ŸÅŸÉŸáÿßŸÜŸä ÿßŸÑŸÉŸàŸäÿ™',
      subtitle: 'ÿ£ŸÅÿ∂ŸÑ ÿßŸÑŸÅŸàÿßŸÉŸá ŸàÿßŸÑÿÆÿ∂ÿßÿ± ÿßŸÑÿ∑ÿßÿ≤ÿ¨ÿ©',
      cart: 'ÿßŸÑÿ≥ŸÑÿ©',
      admin: 'ŸÑŸàÿ≠ÿ© ÿßŸÑÿ•ÿØÿßÿ±ÿ©'
    },
    en: {
      title: 'Fakahani Kuwait',
      subtitle: 'Best Fresh Fruits & Vegetables',
      cart: 'Cart',
      admin: 'Admin Panel'
    }
  };

  const currentTexts = texts[language];

  return (
    <header className="header">
      <div className="header-container">
        {/* Logo and Title */}
        <div className="header-brand">
          <h1 className="header-title">{currentTexts.title}</h1>
          <p className="header-subtitle">{currentTexts.subtitle}</p>
        </div>

        {/* Navigation */}
        <nav className="header-nav">
          {/* Language Toggle */}
          <button
            className="language-toggle"
            onClick={() => onLanguageChange(language === 'ar' ? 'en' : 'ar')}
          >
            {language === 'ar' ? 'EN' : 'ÿπÿ±ÿ®Ÿä'}
          </button>

          {/* Cart Button */}
          <button className="cart-button" onClick={onCartClick}>
            <span className="cart-icon">üõí</span>
            <span className="cart-text">{currentTexts.cart}</span>
            {cartItemsCount > 0 && (
              <span className="cart-count">{cartItemsCount}</span>
            )}
          </button>

          {/* Admin Link */}
          <button className="admin-button" onClick={onAdminClick}>
            <span className="admin-icon">‚öôÔ∏è</span>
            <span className="admin-text">{currentTexts.admin}</span>
          </button>
        </nav>
      </div>
    </header>
  );
};

export default Header;
