import React from 'react';
import { Language, ProductCategory } from '../../types';
import './CategoryFilter.css';

interface CategoryFilterProps {
  selectedCategory: string;
  onCategoryChange: (category: string) => void;
  language: Language;
}

const CategoryFilter: React.FC<CategoryFilterProps> = ({
  selectedCategory,
  onCategoryChange,
  language,
}) => {
  const categories = [
    { key: 'all', ar: 'ÿ¨ŸÖŸäÿπ ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™', en: 'All Products', emoji: 'üõí' },
    { key: 'fruits', ar: 'ŸÅŸàÿßŸÉŸá', en: 'Fruits', emoji: 'üçé' },
    { key: 'vegetables', ar: 'ÿÆÿ∂ÿßÿ±', en: 'Vegetables', emoji: 'ü•ï' },
    { key: 'leafy', ar: 'Ÿàÿ±ŸÇŸäÿßÿ™', en: 'Leafy Greens', emoji: 'ü•¨' },
    { key: 'baskets', ar: 'ÿ≥ŸÑÿßÿ™', en: 'Baskets', emoji: 'üß∫' },
  ];

  return (
    <div className="category-filter">
      <div className="category-buttons">
        {categories.map((category) => (
          <button
            key={category.key}
            className={`category-btn ${selectedCategory === category.key ? 'active' : ''}`}
            onClick={() => onCategoryChange(category.key)}
          >
            <span className="category-emoji">{category.emoji}</span>
            <span className="category-text">
              {language === 'ar' ? category.ar : category.en}
            </span>
          </button>
        ))}
      </div>
    </div>
  );
};

export default CategoryFilter;
