import React, { useState } from 'react';
import { Product, Language } from '../../types';
import './ProductCard.css';

interface ProductCardProps {
  product: Product;
  language: Language;
  onAddToCart: (product: Product, quantity: number) => void;
}

const ProductCard: React.FC<ProductCardProps> = ({
  product,
  language,
  onAddToCart,
}) => {
  const [quantity, setQuantity] = useState(1);
  const [isAdding, setIsAdding] = useState(false);

  const texts = {
    ar: {
      addToCart: 'ÿ£ÿ∂ŸÅ ŸÑŸÑÿ≥ŸÑÿ©',
      quantity: 'ÿßŸÑŸÉŸÖŸäÿ©',
      price: 'ÿØ.ŸÉ',
      outOfStock: 'ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÅÿ±',
      added: 'ÿ™ŸÖ ÿßŸÑÿ•ÿ∂ÿßŸÅÿ©!'
    },
    en: {
      addToCart: 'Add to Cart',
      quantity: 'Quantity',
      price: 'KWD',
      outOfStock: 'Out of Stock',
      added: 'Added!'
    }
  };

  const currentTexts = texts[language];

  const handleAddToCart = () => {
    if (product.stock === 0) return;

    setIsAdding(true);
    onAddToCart(product, quantity);

    // Reset animation after 1 second
    setTimeout(() => {
      setIsAdding(false);
      setQuantity(1);
    }, 1000);
  };

  const categoryEmojis = {
    fruits: 'üçé',
    vegetables: 'ü•ï',
    leafy: 'ü•¨',
    baskets: 'üß∫'
  };

  return (
    <div className={`product-card ${product.stock === 0 ? 'out-of-stock' : ''}`}>
      {/* Product Image */}
      <div className="product-image-container">
        <img
          src={product.image}
          alt={product.name[language]}
          className="product-image"
          loading="lazy"
        />
        <div className="category-badge">
          {categoryEmojis[product.category]}
        </div>
        {product.stock === 0 && (
          <div className="out-of-stock-overlay">
            <span>{currentTexts.outOfStock}</span>
          </div>
        )}
      </div>

      {/* Product Info */}
      <div className="product-info">
        <h3 className="product-name">{product.name[language]}</h3>

        <div className="product-details">
          <div className="price-container">
            <span className="price">
              {product.price.toFixed(3)} {currentTexts.price}
            </span>
            <span className="unit">/ {product.unit[language]}</span>
          </div>

          {product.stock > 0 && product.stock <= 5 && (
            <div className="low-stock-warning">
              ‚ö†Ô∏è {product.stock} ŸÖÿ™ÿ®ŸÇŸä
            </div>
          )}
        </div>

        {/* Quantity & Add to Cart */}
        {product.stock > 0 && (
          <div className="product-actions">
            <div className="quantity-selector">
              <label className="quantity-label">{currentTexts.quantity}:</label>
              <div className="quantity-controls">
                <button
                  className="quantity-btn"
                  onClick={() => setQuantity(Math.max(1, quantity - 1))}
                  disabled={quantity <= 1}
                >
                  -
                </button>
                <span className="quantity-value">{quantity}</span>
                <button
                  className="quantity-btn"
                  onClick={() => setQuantity(Math.min(product.stock, quantity + 1))}
                  disabled={quantity >= product.stock}
                >
                  +
                </button>
              </div>
            </div>

            <button
              className={`add-to-cart-btn ${isAdding ? 'adding' : ''}`}
              onClick={handleAddToCart}
              disabled={isAdding}
            >
              {isAdding ? currentTexts.added : currentTexts.addToCart}
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductCard;
