import React, { useState } from 'react';
import { Product, Language, ProductCategory } from '../../types';
import AdminDashboard from '../AdminDashboard/AdminDashboard.tsx';
import './AdminPanel.css';

interface AdminPanelProps {
  language: Language;
  products: Product[];
  onAddProduct: (product: Omit<Product, 'id'>) => void;
  onUpdateProduct: (id: number, product: Partial<Product>) => void;
  onDeleteProduct: (id: number) => void;
  onClose: () => void;
}

const AdminPanel: React.FC<AdminPanelProps> = ({
  language,
  products,
  onAddProduct,
  onUpdateProduct,
  onDeleteProduct,
  onClose,
}) => {
  const [activeTab, setActiveTab] = useState<'dashboard' | 'list' | 'add' | 'units' | 'settings'>('dashboard');
  const [editingProduct, setEditingProduct] = useState<Product | null>(null);
  const [deliveryPrice, setDeliveryPrice] = useState(2.000);

  // Units management state
  const [units, setUnits] = useState([
    { id: 1, ar: 'ŸÉŸäŸÑŸà', en: 'kg' },
    { id: 2, ar: 'ÿ≠ÿ®ÿ©', en: 'piece' },
    { id: 3, ar: 'ÿ≥ÿ≠ÿßÿ±ÿ©', en: 'bunch' },
    { id: 4, ar: 'ÿπŸÑÿ®ÿ©', en: 'box' },
    { id: 5, ar: 'ŸÉÿ±ÿ™ŸàŸÜ', en: 'carton' },
    { id: 6, ar: 'ÿ¨ÿ±ÿßŸÖ', en: 'gram' },
    { id: 7, ar: 'ŸÑÿ™ÿ±', en: 'liter' }
  ]);
  const [newUnit, setNewUnit] = useState({ ar: '', en: '' });
  const [editingUnit, setEditingUnit] = useState<{ id: number; ar: string; en: string } | null>(null);

  const texts = {
    ar: {
      title: 'ŸÑŸàÿ≠ÿ© ÿßŸÑÿ•ÿØÿßÿ±ÿ©',
      dashboard: 'ŸÑŸàÿ≠ÿ© ÿßŸÑŸÖÿπŸÑŸàŸÖÿßÿ™',
      productList: 'ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑŸÖŸÜÿ™ÿ¨ÿßÿ™',
      addProduct: 'ÿ•ÿ∂ÿßŸÅÿ© ŸÖŸÜÿ™ÿ¨',
      units: 'ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸàÿ≠ÿØÿßÿ™',
      settings: 'ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™',
      close: 'ÿ•ÿ∫ŸÑÿßŸÇ',
      name: 'ÿßÿ≥ŸÖ ÿßŸÑŸÖŸÜÿ™ÿ¨',
      nameAr: 'ÿßŸÑÿßÿ≥ŸÖ ÿ®ÿßŸÑÿπÿ±ÿ®Ÿäÿ©',
      nameEn: 'ÿßŸÑÿßÿ≥ŸÖ ÿ®ÿßŸÑÿ•ŸÜÿ¨ŸÑŸäÿ≤Ÿäÿ©',
      category: 'ÿßŸÑŸÅÿ¶ÿ©',
      price: 'ÿßŸÑÿ≥ÿπÿ± (ÿØ.ŸÉ)',
      unit: 'ÿßŸÑŸàÿ≠ÿØÿ©',
      unitAr: 'ÿßŸÑŸàÿ≠ÿØÿ© ÿ®ÿßŸÑÿπÿ±ÿ®Ÿäÿ©',
      unitEn: 'ÿßŸÑŸàÿ≠ÿØÿ© ÿ®ÿßŸÑÿ•ŸÜÿ¨ŸÑŸäÿ≤Ÿäÿ©',
      stock: 'ÿßŸÑŸÉŸÖŸäÿ© ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©',
      image: 'ÿ±ÿßÿ®ÿ∑ ÿßŸÑÿµŸàÿ±ÿ©',
      description: 'ÿßŸÑŸàÿµŸÅ',
      descriptionAr: 'ÿßŸÑŸàÿµŸÅ ÿ®ÿßŸÑÿπÿ±ÿ®Ÿäÿ©',
      descriptionEn: 'ÿßŸÑŸàÿµŸÅ ÿ®ÿßŸÑÿ•ŸÜÿ¨ŸÑŸäÿ≤Ÿäÿ©',
      published: 'ŸÖŸÜÿ¥Ÿàÿ±',
      save: 'ÿ≠ŸÅÿ∏',
      cancel: 'ÿ•ŸÑÿ∫ÿßÿ°',
      edit: 'ÿ™ÿπÿØŸäŸÑ',
      delete: 'ÿ≠ÿ∞ŸÅ',
      confirm: 'ÿ™ÿ£ŸÉŸäÿØ',
      fruits: 'ŸÅŸàÿßŸÉŸá',
      vegetables: 'ÿÆÿ∂ÿßÿ±',
      leafy: 'Ÿàÿ±ŸÇŸäÿßÿ™',
      baskets: 'ÿ≥ŸÑÿßÿ™',
      deliverySettings: 'ÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑÿ™ŸàÿµŸäŸÑ',
      deliveryPrice: 'ÿ≥ÿπÿ± ÿßŸÑÿ™ŸàÿµŸäŸÑ (ÿØ.ŸÉ)',
      kg: 'ŸÉŸäŸÑŸà',
      bunch: 'ÿ≥ÿ≠ÿßÿ±ÿ©',
      piece: 'ÿ≠ÿ®ÿ©',
      deleteConfirm: 'ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ≠ÿ∞ŸÅ Ÿáÿ∞ÿß ÿßŸÑŸÖŸÜÿ™ÿ¨ÿü',
      unitsManagement: 'ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸàÿ≠ÿØÿßÿ™',
      addUnit: 'ÿ•ÿ∂ÿßŸÅÿ© Ÿàÿ≠ÿØÿ© ÿ¨ÿØŸäÿØÿ©',
      unitsList: 'ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑŸàÿ≠ÿØÿßÿ™',
      unitArabic: 'ÿßŸÑŸàÿ≠ÿØÿ© ÿ®ÿßŸÑÿπÿ±ÿ®Ÿäÿ©',
      unitEnglish: 'ÿßŸÑŸàÿ≠ÿØÿ© ÿ®ÿßŸÑÿ•ŸÜÿ¨ŸÑŸäÿ≤Ÿäÿ©',
      addNewUnit: 'ÿ•ÿ∂ÿßŸÅÿ© Ÿàÿ≠ÿØÿ©',
      editUnit: 'ÿ™ÿπÿØŸäŸÑ ÿßŸÑŸàÿ≠ÿØÿ©',
      saveUnit: 'ÿ≠ŸÅÿ∏ ÿßŸÑŸàÿ≠ÿØÿ©',
      deleteUnit: 'ÿ≠ÿ∞ŸÅ ÿßŸÑŸàÿ≠ÿØÿ©',
      deleteUnitConfirm: 'ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ≠ÿ∞ŸÅ Ÿáÿ∞Ÿá ÿßŸÑŸàÿ≠ÿØÿ©ÿü'
    },
    en: {
      title: 'Admin Panel',
      dashboard: 'Dashboard',
      productList: 'Product List',
      addProduct: 'Add Product',
      units: 'Units Management',
      settings: 'Settings',
      close: 'Close',
      name: 'Product Name',
      nameAr: 'Name in Arabic',
      nameEn: 'Name in English',
      category: 'Category',
      price: 'Price (KWD)',
      unit: 'Unit',
      unitAr: 'Unit in Arabic',
      unitEn: 'Unit in English',
      stock: 'Stock',
      image: 'Image URL',
      description: 'Description',
      descriptionAr: 'Description in Arabic',
      descriptionEn: 'Description in English',
      published: 'Published',
      save: 'Save',
      cancel: 'Cancel',
      edit: 'Edit',
      delete: 'Delete',
      confirm: 'Confirm',
      fruits: 'Fruits',
      vegetables: 'Vegetables',
      leafy: 'Leafy Greens',
      baskets: 'Baskets',
      deliverySettings: 'Delivery Settings',
      deliveryPrice: 'Delivery Price (KWD)',
      kg: 'kg',
      bunch: 'bunch',
      piece: 'piece',
      deleteConfirm: 'Are you sure you want to delete this product?',
      unitsManagement: 'Units Management',
      addUnit: 'Add New Unit',
      unitsList: 'Units List',
      unitArabic: 'Unit in Arabic',
      unitEnglish: 'Unit in English',
      addNewUnit: 'Add Unit',
      editUnit: 'Edit Unit',
      saveUnit: 'Save Unit',
      deleteUnit: 'Delete Unit',
      deleteUnitConfirm: 'Are you sure you want to delete this unit?'
    }
  };

  const currentTexts = texts[language];

  const categories: { key: ProductCategory; label: string }[] = [
    { key: 'fruits', label: currentTexts.fruits },
    { key: 'vegetables', label: currentTexts.vegetables },
    { key: 'leafy', label: currentTexts.leafy },
    { key: 'baskets', label: currentTexts.baskets },
  ];

  // Use the managed units instead of hardcoded options
  const unitOptions = units;

  const [formData, setFormData] = useState({
    nameAr: '',
    nameEn: '',
    category: 'fruits' as ProductCategory,
    price: 0,
    unitAr: 'ŸÉŸäŸÑŸà',
    unitEn: 'kg',
    stock: 0,
    image: '',
    descriptionAr: '',
    descriptionEn: '',
    isPublished: true,
  });

  const resetForm = () => {
    setFormData({
      nameAr: '',
      nameEn: '',
      category: 'fruits',
      price: 0,
      unitAr: 'ŸÉŸäŸÑŸà',
      unitEn: 'kg',
      stock: 0,
      image: '',
      descriptionAr: '',
      descriptionEn: '',
      isPublished: true,
    });
    setEditingProduct(null);
  };

  const handleEdit = (product: Product) => {
    setEditingProduct(product);
    setFormData({
      nameAr: product.name.ar,
      nameEn: product.name.en,
      category: product.category,
      price: product.price,
      unitAr: product.unit.ar,
      unitEn: product.unit.en,
      stock: product.stock,
      image: product.image,
      descriptionAr: product.description?.ar || '',
      descriptionEn: product.description?.en || '',
      isPublished: product.isPublished,
    });
    setActiveTab('add');
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const productData = {
      name: { ar: formData.nameAr, en: formData.nameEn },
      category: formData.category,
      price: formData.price,
      unit: { ar: formData.unitAr, en: formData.unitEn },
      stock: formData.stock,
      image: formData.image,
      description: formData.descriptionAr || formData.descriptionEn ? {
        ar: formData.descriptionAr,
        en: formData.descriptionEn,
      } : undefined,
      isPublished: formData.isPublished,
    };

    if (editingProduct) {
      onUpdateProduct(editingProduct.id, productData);
    } else {
      onAddProduct(productData);
    }

    resetForm();
    setActiveTab('list');
  };

  const handleDelete = (id: number) => {
    if (window.confirm(currentTexts.deleteConfirm)) {
      onDeleteProduct(id);
    }
  };

  const togglePublished = (product: Product) => {
    onUpdateProduct(product.id, { isPublished: !product.isPublished });
  };

  // Units management functions
  const handleAddUnit = () => {
    if (newUnit.ar.trim() && newUnit.en.trim()) {
      const newId = Math.max(...units.map(u => u.id)) + 1;
      setUnits([...units, { id: newId, ...newUnit }]);
      setNewUnit({ ar: '', en: '' });
    }
  };

  const handleEditUnit = (unit: { id: number; ar: string; en: string }) => {
    setEditingUnit(unit);
  };

  const handleSaveUnit = () => {
    if (editingUnit && editingUnit.ar.trim() && editingUnit.en.trim()) {
      setUnits(units.map(u => u.id === editingUnit.id ? editingUnit : u));
      setEditingUnit(null);
    }
  };

  const handleDeleteUnit = (id: number) => {
    if (window.confirm(currentTexts.deleteUnitConfirm)) {
      setUnits(units.filter(u => u.id !== id));
    }
  };

  return (
    <div className="admin-overlay" onClick={onClose}>
      <div className="admin-container" onClick={(e) => e.stopPropagation()}>
        <div className="admin-header">
          <h2>{currentTexts.title}</h2>
          <button className="close-btn" onClick={onClose}>‚úï</button>
        </div>

        <div className="admin-tabs">
          <button
            className={`tab-btn ${activeTab === 'dashboard' ? 'active' : ''}`}
            onClick={() => setActiveTab('dashboard')}
          >
            üìä {currentTexts.dashboard}
          </button>
          <button
            className={`tab-btn ${activeTab === 'list' ? 'active' : ''}`}
            onClick={() => setActiveTab('list')}
          >
            üìã {currentTexts.productList}
          </button>
          <button
            className={`tab-btn ${activeTab === 'add' ? 'active' : ''}`}
            onClick={() => setActiveTab('add')}
          >
            ‚ûï {currentTexts.addProduct}
          </button>
          <button
            className={`tab-btn ${activeTab === 'units' ? 'active' : ''}`}
            onClick={() => setActiveTab('units')}
          >
            üìè {currentTexts.units}
          </button>
          <button
            className={`tab-btn ${activeTab === 'settings' ? 'active' : ''}`}
            onClick={() => setActiveTab('settings')}
          >
            ‚öôÔ∏è {currentTexts.settings}
          </button>
        </div>

        <div className="admin-content">
          {activeTab === 'dashboard' && (
            <AdminDashboard language={language} />
          )}

          {activeTab === 'list' && (
            <div className="product-list">
              {products.map((product) => (
                <div key={product.id} className={`product-item ${!product.isPublished ? 'unpublished' : ''}`}>
                  <div className="product-info">
                    <img src={product.image} alt={product.name[language]} className="product-thumb" />
                    <div className="product-details">
                      <h4>{product.name[language]}</h4>
                      <p>{product.price.toFixed(3)} ÿØ.ŸÉ / {product.unit[language]}</p>
                      <p>ÿßŸÑŸÉŸÖŸäÿ©: {product.stock}</p>
                      <span className={`status ${product.isPublished ? 'published' : 'unpublished'}`}>
                        {product.isPublished ? 'üü¢ ŸÖŸÜÿ¥Ÿàÿ±' : 'üî¥ ŸÖÿÆŸÅŸä'}
                      </span>
                    </div>
                  </div>
                  <div className="product-actions">
                    <button
                      className="toggle-btn"
                      onClick={() => togglePublished(product)}
                    >
                      {product.isPublished ? 'üëÅÔ∏è' : 'üôà'}
                    </button>
                    <button
                      className="edit-btn"
                      onClick={() => handleEdit(product)}
                    >
                      ‚úèÔ∏è
                    </button>
                    <button
                      className="delete-btn"
                      onClick={() => handleDelete(product.id)}
                    >
                      üóëÔ∏è
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}

          {activeTab === 'add' && (
            <form className="product-form" onSubmit={handleSubmit}>
              <div className="form-row">
                <div className="form-group">
                  <label>{currentTexts.nameAr}</label>
                  <input
                    type="text"
                    value={formData.nameAr}
                    onChange={(e) => setFormData({ ...formData, nameAr: e.target.value })}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>{currentTexts.nameEn}</label>
                  <input
                    type="text"
                    value={formData.nameEn}
                    onChange={(e) => setFormData({ ...formData, nameEn: e.target.value })}
                    required
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>{currentTexts.category}</label>
                  <select
                    value={formData.category}
                    onChange={(e) => setFormData({ ...formData, category: e.target.value as ProductCategory })}
                  >
                    {categories.map((cat) => (
                      <option key={cat.key} value={cat.key}>
                        {cat.label}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="form-group">
                  <label>{currentTexts.price}</label>
                  <input
                    type="number"
                    step="0.001"
                    value={formData.price}
                    onChange={(e) => setFormData({ ...formData, price: parseFloat(e.target.value) })}
                    required
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>{currentTexts.unitAr}</label>
                  <select
                    value={formData.unitAr}
                    onChange={(e) => {
                      const selectedUnit = unitOptions.find(unit => unit.ar === e.target.value);
                      setFormData({
                        ...formData,
                        unitAr: e.target.value,
                        unitEn: selectedUnit?.en || 'kg'
                      });
                    }}
                  >
                    {unitOptions.map((unit) => (
                      <option key={unit.ar} value={unit.ar}>
                        {unit.ar}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="form-group">
                  <label>{currentTexts.stock}</label>
                  <input
                    type="number"
                    value={formData.stock}
                    onChange={(e) => setFormData({ ...formData, stock: parseInt(e.target.value) })}
                    required
                  />
                </div>
              </div>

              <div className="form-group">
                <label>{currentTexts.image}</label>
                <input
                  type="url"
                  value={formData.image}
                  onChange={(e) => setFormData({ ...formData, image: e.target.value })}
                  placeholder="https://example.com/image.jpg"
                  required
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>{currentTexts.descriptionAr}</label>
                  <textarea
                    value={formData.descriptionAr}
                    onChange={(e) => setFormData({ ...formData, descriptionAr: e.target.value })}
                    rows={3}
                  />
                </div>
                <div className="form-group">
                  <label>{currentTexts.descriptionEn}</label>
                  <textarea
                    value={formData.descriptionEn}
                    onChange={(e) => setFormData({ ...formData, descriptionEn: e.target.value })}
                    rows={3}
                  />
                </div>
              </div>

              <div className="form-group">
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    checked={formData.isPublished}
                    onChange={(e) => setFormData({ ...formData, isPublished: e.target.checked })}
                  />
                  {currentTexts.published}
                </label>
              </div>

              <div className="form-actions">
                <button type="button" className="cancel-btn" onClick={resetForm}>
                  {currentTexts.cancel}
                </button>
                <button type="submit" className="save-btn">
                  {currentTexts.save}
                </button>
              </div>
            </form>
          )}

          {activeTab === 'units' && (
            <div className="units-panel">
              <div className="units-section">
                <h3>{currentTexts.unitsManagement}</h3>

                {/* Add New Unit Form */}
                <div className="add-unit-form">
                  <h4>{currentTexts.addUnit}</h4>
                  <div className="unit-form-row">
                    <div className="form-group">
                      <label>{currentTexts.unitArabic}</label>
                      <input
                        type="text"
                        value={newUnit.ar}
                        onChange={(e) => setNewUnit({ ...newUnit, ar: e.target.value })}
                        placeholder="ŸÖÿ´ÿßŸÑ: ŸÉŸäŸÑŸà"
                      />
                    </div>
                    <div className="form-group">
                      <label>{currentTexts.unitEnglish}</label>
                      <input
                        type="text"
                        value={newUnit.en}
                        onChange={(e) => setNewUnit({ ...newUnit, en: e.target.value })}
                        placeholder="Example: kg"
                      />
                    </div>
                    <button 
                      className="add-unit-btn"
                      onClick={handleAddUnit}
                      disabled={!newUnit.ar.trim() || !newUnit.en.trim()}
                    >
                      ‚ûï {currentTexts.addNewUnit}
                    </button>
                  </div>
                </div>

                {/* Units List */}
                <div className="units-list">
                  <h4>{currentTexts.unitsList}</h4>
                  <div className="units-table">
                    <div className="units-header">
                      <div className="col">{currentTexts.unitArabic}</div>
                      <div className="col">{currentTexts.unitEnglish}</div>
                      <div className="col">ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™</div>
                    </div>
                    {units.map((unit) => (
                      <div key={unit.id} className="unit-row">
                        {editingUnit && editingUnit.id === unit.id ? (
                          <>
                            <div className="col">
                              <input
                                type="text"
                                value={editingUnit.ar}
                                onChange={(e) => setEditingUnit({ ...editingUnit, ar: e.target.value })}
                              />
                            </div>
                            <div className="col">
                              <input
                                type="text"
                                value={editingUnit.en}
                                onChange={(e) => setEditingUnit({ ...editingUnit, en: e.target.value })}
                              />
                            </div>
                            <div className="col actions">
                              <button 
                                className="save-btn"
                                onClick={handleSaveUnit}
                              >
                                ‚úÖ {currentTexts.saveUnit}
                              </button>
                              <button 
                                className="cancel-btn"
                                onClick={() => setEditingUnit(null)}
                              >
                                ‚ùå {currentTexts.cancel}
                              </button>
                            </div>
                          </>
                        ) : (
                          <>
                            <div className="col unit-ar">{unit.ar}</div>
                            <div className="col unit-en">{unit.en}</div>
                            <div className="col actions">
                              <button 
                                className="edit-btn"
                                onClick={() => handleEditUnit(unit)}
                              >
                                ‚úèÔ∏è {currentTexts.edit}
                              </button>
                              <button 
                                className="delete-btn"
                                onClick={() => handleDeleteUnit(unit.id)}
                              >
                                üóëÔ∏è {currentTexts.delete}
                              </button>
                            </div>
                          </>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'settings' && (
            <div className="settings-panel">
              <div className="settings-section">
                <h3>{currentTexts.deliverySettings}</h3>
                <div className="form-group">
                  <label>{currentTexts.deliveryPrice}</label>
                  <input
                    type="number"
                    step="0.001"
                    value={deliveryPrice}
                    onChange={(e) => setDeliveryPrice(parseFloat(e.target.value))}
                  />
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
